var TQ = TQ || {};
TQ.AudioRecorder = (function() {
  var CLOSE_MIC_WHEN_PENDING = true;
  var STATE_UNKNOWN = 0;
  var STATE_INITIALIZED = 1;
  var STATE_STARTED = 2;
  var STATE_PENDING = 3;
  var recorder = null;
  var state = STATE_UNKNOWN;
  var onStopCallback;

  return {
    get isPending() {
      return state === STATE_PENDING;
    },
    get isRecording() {
      return state === STATE_STARTED;
    },
    accept: accept,
    close: close,
    start: start,
    stop: stop
  };

  function init(onSuccess, onError) {
    recorder = new Recorder({
      sampleRate: 44100, // 44100Hz(MP3)
      bitRate: 128, // 128kbps(MP3)
      success: function() { // //
        state = STATE_INITIALIZED;
        console.log("!");
        if (onSuccess) {
          onSuccess();
        }
      },

      error: function(msg) { // 
        alert(msg);
        if (onError) {
          onError();
        }
      },
      fix: function(msg) { // H5
        alert(msg);
        if (onError) {
          onError();
        }
      }
    });
  }

  function start(callback, refreshUI) {
    if (state >= STATE_INITIALIZED && state !== STATE_PENDING) {
      if (state === STATE_STARTED) { // pending 
        return;
      }
      state = STATE_STARTED;
      onStopCallback = callback;
      recorder && recorder.start();
      if (refreshUI) {
        refreshUI();
      }
    } else {
      init(function() {
        start(callback, refreshUI);
      });
    }
  }

  function stop() {
    if (state < STATE_STARTED) {
      return setTimeout(function() {
        stop();
      });
    }

    state = STATE_PENDING;
    if (!onStopCallback) {
      onStopCallback = defaultCallback;
    }

    recorder && recorder.stop();
    recorder && recorder.getBlob(function(data) {
      onStopCallback(data);
      onStopCallback = null;
      setTimeout(function() {
        recorder.close();
      });
    });
  }

  function accept() {
    if (state === STATE_STARTED) {
      stop();
    }
    // recorder.close();
    state = STATE_UNKNOWN;
  }

  function defaultCallback(blob) {
    console.log("sound recorded: type = " + blob.type + ", size = " + blob.size);
    var audio = document.createElement("audio");
    audio.src = URL.createObjectURL(blob);
    audio.controls = true;
    document.appendChild(audio);
  }
})();

