    POINT: "Point",
    RAIN: "RainEffect", // TBD
    SNOW: "SnowEffect", // TBD
    RECTANGLE: "Rectangle",
    CIRCLE: "Circle",
    SOUND: "SOUND",
    TEXT: "Text",
