  function isSelectedEvent(e) {
    if (hasTouch) {
      if (e.type === "click") {
        return false;
      }
    }

    if (hasMouse) {
      if (e.type === "touchstart") {
        return false;
      }
    }

    if (e.type === "touchstart") {
      hasTouch = true;
    } else if (e.type === "click") {
      hasMouse = false;
    } else {
      console.error("wrong events: " + e.type);
    }

    return true;
  }

